  //INSIDE HOME COMPONENT
  if (action.type === ADD_TO_CART) {
    console.log('state', state);
    console.log('action in reducer', action);
    // return {
    //   ...state,
    //   addedItems: [...state.addedItems, addedItem],
    //   total: newTotal
    // };
    return state;
  } else {
    return state;
  }
};

export default cartReducer;
