import React, { Component } from 'react';
import Link from 'next/link';

import Image from './Image';
import { connect } from 'react-redux';

import { addToCart } from '../actions/cartActions';

class ProductCard extends Component {
  render() {
    <div>
      <div className="ui card">
        <div className="image">
          <Image src={this.props.product.imageUrl} className="thumbnail" />
        </div>
        <Link href="/p/[_id]" as={`/p/${product._id}`}>
          <div className="content">
            <a className="header">{product.name}</a>
            <div className="meta">
              <span className="price">${product.price}</span>
            </div>
            <div className="description">{product.description}</div>
          </div>
        </Link>
        <div
          className="ui teal button"
          onClick={() => {
            console.log(`Adding to cart:${product._id}`);
            addToCart(product._id);
          }}
        >
          <i className="shop icon"></i> Add to Cart
        </div>
      </div>

      <style jsx>{`
        a {
          text-decoration: none;
          color: blue;
          font-family: 'Arial';
        }

        a:hover {
          opacity: 0.6;
        }
        .product-info {
          display: flex;
          justify-content: space-between;
        }
      `}</style>
    </div>;
  }
}

const mapStateToProps = state => {
  return {
    items: state.items,
    product: state.product
  };
};

const mapDispatchToProps = dispatch => {
  console.log('dispatching ', dispatch);
  return {
    addToCart: id => {
      dispatch(addToCart(id));
    }
  };
};

export default connect(mapDispatchToProps, mapStateToProps)(ProductCard);
